from heapq import heappop, heappushdef find_path (source_point, destination_point, mesh):    """    Searches for a path from source_point to destination_point through the mesh    Args:        source_point: starting point of the pathfinder        destination_point: the ultimate goal the pathfinder must reach        mesh: pathway constraints the path adheres to    Returns:        A path (list of points) from source_point to destination_point if exists        A list of boxes explored by the algorithm    """    print(source_point)    print(destination_point)    path = []    boxes = {}    came_from = {}    startBox = None    destBox = None    startFound = False    destFound = False    test = False    for box in mesh['boxes']:        if box[0] <= source_point[1] and source_point[1] <= box[1] and box[2] <= source_point[0] and source_point[0] <= box[3]:                            startBox = box            print("START FOUND: ", box)            startFound = True            came_from[box] = None        if box[0] <= destination_point[1] and destination_point[1] <= box[1] and box[2] <= destination_point[0] and destination_point[0] <= box[3]:            print("DESTINATION FOUND: ", box)            destFound = True            destBox = box                    if startFound and destFound:            test = True            break    queue = [startBox]    while queue and test:        current = heappop(queue)        if current == destBox:            while current != startBox:                boxes[current] = came_from[current]                current = came_from[current]            boxes[startBox] = None            break        for next in mesh['adj'][current]:            if next not in came_from:                heappush(queue, next)                came_from[next] = current                # #TODO IF TIME: Exit the moment user clicks on dark line        if len(queue) == 0:            print('path not found')                print(boxes)    #             return path, boxes.keys()